import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";

const UserDashboard = () => {
  const { id } = useParams();
  const [user, setUser] = useState({});

  const getUser = async () => {
    try {
      const resp = await fetch(`/user/${id}`);
      const data = await resp.json();
      setUser(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    getUser();
  }, []);

  return (
    <div className="container my-3">
      <h2>User Profile</h2>
      <div className="userdetail">
        <div>
          <span>Username</span> kenneth
        </div>
        <div>
          <span>Email</span> ken@gmail.com
        </div>
        <div>
          <span>Password</span> password details
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
