import React, { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";

const EditUser = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
  });

  useEffect(() => {
    const getUser = async () => {
      try {
        const resp = await fetch(`/user/${id}`);
        const data = await resp.json();
        setUser(data);
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };
    getUser();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { username, email, password } = values;
    try {
      await fetch("/user/create", {
        method: "PUT",
        credentials: "include",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, email, password }),
      });
      navigate("/admin", { msg: "user detail successfully updated" });

      //  .then(response => response.json())
    } catch (error) {
      console.log(error);
    }

    const userDetail = { username, password, email };
    console.log(userDetail);
  };

  return (
    <div className="container my-3">
      <h2 className="my-3">Edit user</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <label className="mb-1" htmlFor="username">
            Username
          </label>
          <input
            type="text"
            name="username"
            value={values.username}
            placeholder={user.username}
            className="form-control"
            onChange={handleChange}
          />
        </div>
        <div className="form-group mb-3">
          <label className="mb-1" htmlFor="email">
            Email
          </label>
          <input
            type="email"
            name="email"
            placeholder={user.email}
            className="form-control"
            value={values.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group mb-3">
          <label className="mb-1" htmlFor="author">
            Password
          </label>
          <input
            type="password"
            name="password"
            placeholder={user.password}
            className="form-control"
            value={values.password}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="btn btn-primary text-light mt-3">
          Update
        </button>
      </form>
      <Link className="btn btn-outline-primary mt-3" to="/admin">
        Cancel
      </Link>
    </div>
  );
};

export default EditUser;
