import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";

const UserDashboard = () => {
  const { id } = useParams();
  const [user, setUser] = useState({});

  useEffect(() => {
    const getUser = async () => {
      try {
        const resp = await fetch(`/user/${id}`);
        const data = await resp.json();
        setUser(data);
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };
    getUser();
  }, [id]);

  return (
    <div className="container my-3">
      <h2>User Profile</h2>
      <div className="userdetail">
        <div className="my-3">
          <span>Username: </span> kenneth
        </div>
        <div className="my-3">
          <span>Email: </span> ken@gmail.com
        </div>
        <div className="my-3">
          <span>Password: </span> password details
        </div>
      </div>
      <Link to={`/user/edit/ ${user.id}`} className="btn btn-info">
        Update
      </Link>
    </div>
  );
};

export default UserDashboard;
